mapper.select:
SELECT r.`commodityId`,r.`cName`,r.`inventory`,r.minimumStock ,u.`unitId`,b.`brandId`,s.`supplierId`,u.`uName` AS repertoryUName,b.`bName` AS repertoryBName,s.`sName` AS repertorySName,s.`sAddress`,s.`sContacts`,s.`sPhone`,s.`comments`,r.`laidTime`,se.`stoName` AS repertorySeName FROM repertory AS r INNER JOIN commodity AS c ON r.commodityId = c.commodityId INNER JOIN unit AS u ON r.unitId = u.unitId INNER JOIN brand AS b ON b.`brandId`=r.`brandId` INNER JOIN supplier AS s ON r.`supplierId`=s.`supplierId` INNER JOIN storehouse AS se ON r.`storehouseId` = se.`storehouseId`
and r.storehouseId=#{storehouseId}
and r.unitId=#{unitId}
and r.brandId=#{brandId}
limit #{startRow},#{pageSize}

SELECT COUNT(1) FROM repertory AS r INNER JOIN commodity AS c ON r.commodityId = c.commodityId INNER JOIN unit AS u ON r.unitId = u.unitId INNER JOIN brand AS b ON b.`brandId`=r.`brandId` INNER JOIN supplier AS s ON r.`supplierId`=s.`supplierId` INNER JOIN storehouse AS se ON r.`storehouseId` = se.`storehouseId`
and r.storehouseId=#{storehouseId}
and r.unitId=#{unitId}
and r.brandId=#{brandId}
