mapper.select:
select p.procurementId,p.billingTime,s.sName as sName,sto.stoName as stoName,u.surname,p.condition,p.comments from procurement p left join supplier s on p.supplierId=s.supplierId left join storehouse sto on p.storehouseId=sto.storehouseId left join user u on p.userId=u.id
and p.procurementId like CONCAT('%',#{procurementId},'%')
and p.condition=#{condition}
and p.billingTime >= #{begintime}
and p.billingTime BETWEEN #{begintime} and #{endtime}
and p.billingTime <= #{endtime}
limit #{start},#{end}

select count(1) from procurement p left join supplier s on p.supplierId=s.supplierId left join storehouse sto on p.storehouseId=sto.storehouseId left join user u on p.userId=u.id
and p.procurementId like CONCAT('%',#{procurementId},'%')
and p.condition=#{condition}
and p.billingTime >= #{begintime}
and p.billingTime BETWEEN #{begintime} and #{endtime}
and p.billingTime <= #{endtime}
